void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec4 dist = abs(uv.y - texture(iChannel0, vec2(uv.x, 0)));
    fragColor = smoothstep(2.0 / iResolution.y, 0.0, dist);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    if (iFrame == 0) {
        float x1 = fragCoord.x / iResolution.x * 3.1415;
        float x2 = x1 - 1.0;
        fragColor = vec4(sin(x1), cos(x1), 0.5 * exp(-10.0 * x2 * x2), 0);
    } else {
        ivec2 thisCoord = ivec2(fragCoord);
        fragColor = texelFetch(iChannel0, thisCoord, 0);
        if ((thisCoord.x & 1) == (iFrame & 1)) {
            int nextIndex = thisCoord.x + 1;
            if (nextIndex < int(iResolution.x)) {
                vec4 nextBin = texelFetch(iChannel0, ivec2(nextIndex, 0), 0);
                fragColor = min(fragColor, nextBin);
            }
        } else {
            int prevIndex = thisCoord.x - 1;
            if (prevIndex >= 0) {
                vec4 prevBin = texelFetch(iChannel0, ivec2(prevIndex, 0), 0);
                fragColor = max(prevBin, fragColor);
            }
        }
    }
}
