vec3 palette( float t ) {
    vec3 vert_offset = vec3(1.5, 0.5, 0.5);
    vec3 amplitude = vec3(1.5, 0.5, 0.5);
    vec3 frequence = vec3(1, 1, 1);
    vec3 phase = vec3(0, 1.33, 0.66);

    return vert_offset + amplitude * cos(6.28318 * (frequence * t + phase));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 3.0 - iResolution.xy) / iResolution.y;
    float angle = (uv.y > 0.0 ? 1.0 : -1.0) *  acos(uv.x/length(uv));
    uv = vec2(sin(iTime + angle), sin(iTime + angle)) / length(uv);
    vec2 uv0 = uv;
    vec3 finalCol = vec3(0);

    for (int i = 0; i < 2; i++) {
        uv = fract(2.0 * uv) - 0.5;

        float dist = length(uv);

        vec3 col = palette(length(uv0) + iTime);


        float val = sin(10.0*dist + iTime)/8.0;
        val = abs(val);
        val = .02/val;

        finalCol += col * val;
    }

    finalCol *= 1.0 - smoothstep(0.0, 8.0, length(uv0));


    fragColor = vec4(finalCol, 0);
}
