void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    if(iTime < 14.){
        uv *= iTime;
    }
    else{
        uv *= 14.;
    }

    uv.x -= iResolution.x + iTime * 0.85;
    uv.y += iResolution.y - iTime * 0.65;

    //change this values to change the pattern, 
    //changing this values may require color adjustments
    float ampX = 100.0;
    float ampY = 70.0;

    float waveLX = 1.44;
    float waveLY = 1.27;

    float freqX = 2./waveLX;
    float freqY = 2./waveLY;

    float sX = 0.2 * freqX;
    float sY = 0.05 * freqY;


    ////////////////////////////////////////////////////

    // overall speed

    float tX = iTime * 3.6;
    float tY = iTime * 10.5;

    ////////////////////

    float c;

    //you can play with the timeOff increment values to change the animation.
    for(int i = 0; i < 10; i++)
    {
        c += ampX * (sin( (uv.x * freqX) + tX * sX));
        c += ampY * (sin( (uv.y * freqY) + tY * sY));

        c += ampX * (sin( ((uv.y + uv.x) * freqX) + tX * sX));
        c += ampX * (sin( ((uv.y - uv.x) * freqX) + tX * sX));


        //c += ampY * (sin( ((uv.y - uv.x) * freqY) + tY * sY));
        //c += ampY * (sin( ((uv.y + uv.x) * freqY) + tY * sY));


        if(i == 5){
            tX*= -1.;
            tY*= -1.;
        }else{
            tX *= 1.53232;
            tY *= 1.99;
        }

        //change this values to change the pattern
        ampX *= 0.76;
        ampY *= 0.65;
        freqX *= 1.215;
        freqY *= 1.612;
    }

    fragColor.r = (max(.0 - 0.5 * ( (c*c) ), 0.0))
    +(max(1.5 - 0.25 * ( abs(c) ), 0.1))
    -(max(5. - .0005 * ((c) ), 0.))
    +(max(0.0 - 0.01 * tan(c), .4))
    +(max(0.1 - 0.0006 * c, .0))
    ;

    fragColor.g = (max(.0 - .1 * (abs(c*c*c) ), 0.3 ))
    +(max(.40 - .05 * ( abs(c) ), 0.3))
    -(max(.0 - .5 * (c*c), 0.5))
    +(max(0.0 - 0.0105 * tan(c), .55))
    +(max(0.1 - 0.00015 * abs(c), .0));

    fragColor.b = (1.);

    fragColor.a = 1.0;
}
