// GrayFractals
// Credits to Radian628 on Youtube, tutorial here: https://www.youtube.com/watch?v=8bbTkNZYdQ8
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float angle = cos(iTime) * 0.22;
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    for (float i = 0.0; i < 50.0; i += 1.0) {
        uv = abs(uv);
        uv -= 0.5;
        uv *= 1.15;
        uv *= mat2(
            cos(angle), -sin(angle),
            sin(angle), cos(angle)
        );
    }
    fragColor = vec4(vec3(length(uv)), 1.0);
}
