void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Integer between 0 and 256^3 - 1
    int x = int(uv.x * 16777215.0);

    // Decompose integer into 3 bytes, one byte for each channel
    int r = (x >> 16) & 0xFF;
    int g = (x >> 8) & 0xFF;
    int b = x & 0xFF;

    // Normalize
    vec3 col = vec3(r, g, b) / 255.0;

    // Output to screen
    fragColor = vec4(col, 1.0);
}
