void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 texCol = texture(iChannel0, uv);


    int radius = 5;
    float sum = 0.;

    vec4 pillSum = vec4(0.);

    for(int x = -radius; x <= radius; x++){
        for(int y = -radius; y <= radius; y++){
            if(sqrt(pow(float(x), 2.) + pow(float(y), 2.)) <= float(radius)){
                vec2 newUV = (fragCoord + vec2(x,y))/iResolution.xy;
                pillSum += texture(iChannel0, newUV);
                sum += 1.;
            }
        }   
    }

    if(uv.x < 0.5){
        fragColor = vec4(texCol);
    } else {
        fragColor = vec4(pillSum / sum);
    }
}
