// RGB River 2 by technochroma
// original code Acid Beach by jarble https://www.shadertoy.com/view/3syyDD
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

#define AA 3 

    vec3 col;
    float t;

    for(int c=0;c<22;c++){
	    vec2 uv = (fragCoord*15.0-iResolution.xy)/iResolution.y;
        t = iTime;
        for(int i=6;i<9;i++)
        {
            uv /= 1.25;
            uv += acos(col.yx);
        	uv += float(i) + (log(uv.x)*cos(uv.y)+cos(uv.y)*sin(iTime)+cos(iTime)*sin(uv.x));
        }
     col[c] = (tan(uv.x+uv.y+iTime));
	}

    fragColor = vec4(col,1.0);

	vec2 uv = fragCoord;

    // small bit of anti-aliasing (fixed 2)
    // drop this in voidmain after fragcolor

	vec2 of = vec2(0.3);

    #ifdef AA
	const float aa = float(AA);
    #else
    const float aa = 1.0;


	for(float i = 0.0; i < aa - 1.0; i++) {

		// super-sample around the center of the pixel.
		vec2 p = (-iResolution.xy + 2.0*(uv + of))/iResolution.y;
        col += render(p);
		of *= r(3.14159/8.0);

        	}

	col /= aa;

	col += 0.2*clamp(col, 0.0, 0.5);
	col = pow(col, vec3(1.0/2.2));

    #endif 

}
