// exploring colors - mouse to unfocus/focus beams of light
//
// *epilepsy warning*

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 last = textureLod(iChannel1, uv, 0.0f).rgb;
    vec3 color = textureLod(iChannel0, uv, 0.0f).rgb;

    color = 1.0f - abs(color - last);

    fragColor = vec4(color,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = 2.0f * uv - 1.0f;
    uv.y *= iResolution.y/iResolution.x;

    uv *= SCALE;

    vec3 color = vec3(0);

    color = hueshift(vec3(uv, uv.x-uv.y), iTime*iTime);

    vec3 last = textureLod(iChannel0, fragCoord/iResolution.xy, 0.0f).rgb;

    color = color + last * 0.99f;

    fragColor = vec4(color,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = 2.0f * uv - 1.0f;
    uv.y *= iResolution.y/iResolution.x;

    uv *= SCALE;

    vec3 color = vec3(0);

    color = hueshift(vec3(uv, uv.x-uv.y), (iTime*iTime + timestep));

    vec3 last = textureLod(iChannel0, fragCoord/iResolution.xy, 0.0f).rgb;

    color = color + last * 0.99f;

    fragColor = vec4(color,1.0);
}

