void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = 2.0f * uv - 1.0f;
    uv.y *= iResolution.y/iResolution.x;

    uv *= SCALE;

    vec3 color = vec3(0);

    color = hueshift(vec3(uv, uv.x-uv.y), (iTime*iTime + timestep * 3.0f));

    vec3 last = textureLod(iChannel0, fragCoord/iResolution.xy, 0.0f).rgb;

    color = color + last * 0.99f;

