}

#define GOLDEN_RATIO (1.61803398874989484820)
#define PI (3.14159265358979323846)

#define timestep (0.7e4 * GOLDEN_RATIO)

#define CONSTANT (0.55735)
#define SCALE (3.0f * iMouse.y/iResolution.y + 1.0f)

vec3 hueshift(in vec3 Color, in float Shift)
{
    vec3 P = vec3(CONSTANT)*dot(vec3(CONSTANT),Color);

    vec3 U = Color-P;

    vec3 V = cross(vec3(CONSTANT),U);    

    Color = U*cos(Shift*2.0*PI) + V*sin(Shift*2.0*PI) + P;

    return Color;
}
