uniform vec2 u_resolution;
uniform vec2 u_mouse;
uniform float u_time;

vec3 random (float st) {
    return fract(sin(vec3(st * 5341.1 + 12.1, st * 5336.2 + 13.2, st * 4538.3 + 14.3)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy) * 2. - 1.;

    float xsize = 0.15;
    float ysize = 0.15;

    float xtime = 9.;
    float ytime = 16.;
    float globaltime = 0.02;

    uv.x += (-1. + xsize * 2.) + (abs(fract(iTime * xtime * globaltime) * 2. - 1.) - 0.) * (1. - (-1. + xsize * 2.)) / (1. - 0.);
    uv.y += (-1. + ysize * 2.) + (abs(fract(iTime * ytime * globaltime) * 2. - 1.) - 0.) * (1. - (-1. + ysize * 2.)) / (1. - 0.);

    float x1 = step(abs(uv.x+-0.15),0.15);
    float y1 = step(abs(uv.y+-0.15),0.15);

    vec3 col1 = random(round(0.64*iTime-0.5));
    vec3 col2 = random(round(0.36*iTime-0.5));
    vec3 col3 = col1 * col2;

    float xy1 = x1 * y1;
    vec3 xy2 = xy1 * col3;

    fragColor = vec4(xy2,1.);
}
