vec2 zexp(vec2 A)
{
    return vec2(pow(2.719, A.x)*cos(A.y), pow(2.719, A.x)*sin(A.y));
}
vec2 zlog(vec2 A)
{
    return vec2(log(A.x*A.x+A.y*A.y)/log(2.719)/2., atan(A.y, A.x));
}
vec2 mult(vec2 A, vec2 B)
{
    return vec2(A.x*B.x-A.y*B.y, A.x*B.y+A.y*B.x);
}
vec2 zinvert(vec2 A)
{
    return vec2(A.x/(A.x*A.x+A.y*A.y), A.y/(A.x*A.x+A.y*A.y));
}
vec2 div(vec2 A, vec2 B)
{
    return mult(A, zinvert(B));
}
vec2 zpow(vec2 A, vec2 B)
{
    return zexp(mult(zlog(A), B));
}
vec2 zsin(vec2 A)
{
    return vec2(sin(A.x)*cosh(A.y), cos(A.x)*sinh(A.y));
}
vec2 zcos(vec2 A)
{
    return vec2(cos(A.x)*cosh(A.y), -sin(A.x)*sinh(A.y));
}
vec2 ztan(vec2 A)
{
    return div(cos(A), sin(A));
}
float zabs(vec2 A)
{
    return sqrt(A.x*A.x+A.y*A.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 m = vec2(iMouse.x/iResolution.x, iMouse.y/iResolution.y);
    float scale = iTime/1.0; 
