    if(iTime/1.0 > 4.0){
        p = vec2(iTime/10.0 + 4.0*24.0/10.0 - 8.0, 0.0);
    }
    while(sqrt(uv.x*uv.x+uv.y*uv.y) < 16.0 && i < maxi){
        uv = zpow(uv, p)+c;
        i = i+1.;
        if(zabs(uv)>2.0){
            d = 1.;
        }
    }
    if(i<maxi)
    {
        float zn = log(sqrt(uv.x*uv.x+uv.y*uv.y));
        float noose = log(zn/zabs(zlog(p)))/zabs(zlog(p));
        i = i-noose + 1.0;
    }
    vec4 colourA = vec4(uv.x, uv.y, 0, 0);
    i = pow(i, 0.6);
    vec4 colourB = vec4(pow(i,0.4)/6.0, pow(i,0.8)/7.0, pow(i,0.6)/4.0, pow(i,0.5)/5.0);
    vec4 colourC = 0.5 + 0.5*cos( 3.0 + i*0.15 + vec4(0.0,0.6,1.0, 0.0));
    // Output to screen
    fragColor = colourC*pow(log(10.0*zabs(p)), 0.5)*1.5;
        if(d == 0.0){
    fragColor = vec4(0,0,0,0);
    }
}
