void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

vec2 uv = fragCoord/iResolution.yy*70.;
float numofp=100.;
float col=0.;


for(float i=0.;i<numofp;i+=1.){

        vec4 cp=texture(iChannel0,vec2(mod(i,2.7)/2.7,i/numofp));
        vec2 pos= cp.xy*iResolution.xy/iResolution.yy*70.;
        float circle=length(pos-uv);
        float flicker = sin(iTime*0.3+(texture(iChannel1,vec2(i/numofp,3)).x)*iTime*0.3);

        float d=circle-1.7;
        d=-d;
        d=max(pow(abs(d),5.2)*d,0.);

        d=d*flicker;
        col = d+col;

}




fragColor=vec4(vec3(1.,0.3,0.1)*col,1); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 

vec2 uv = fragCoord.xy / iResolution.xy;

vec4 cp=texture(iChannel0,uv);

if(cp==vec4(0.,0.,0.,0.)){
    cp=texture(iChannel1,uv);
    vec2 vel=cp.zw;
    vel=vel-vec2(0.5,0.5);
    vel.x=vel.x*0.08;
    vel.y=vel.y*0.08;
    vel=vel+vec2(0.5,0.5);
    cp.zw=vel;
}

cp=cp*1000.;
cp=cp-vec4(0,0,500.,500.);

for(int i=0;i<50;i++){
cp.xy=cp.xy+cp.zw*iTimeDelta;

cp.w=cp.w-0.1*iTimeDelta;

if(cp.y<0.){
cp.y=0.;
cp.w=-cp.w;
} 


if(cp.x<0.){
cp.x=0.;
cp.z=-cp.z;
}


if(cp.y>1000.){
cp.y=1000.;
cp.w=-cp.w;
} 


if(cp.x>1000.){
cp.x=1000.;
cp.z=-cp.z;
}

}
fragColor=vec4((cp+vec4(0,0,500.,500.))/1000.);


}
