float random(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}


float smoothness = 5.0;

void mainImage ( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord;

    float smoothsize = smoothness * smoothness;

    float randomSum = 0.0;

    for (float x=uv.x-(smoothness/2.0); x<uv.x+(smoothness/2.0)+1.0; x++)
    {
        for (float y=uv.y-(smoothness/2.0); y<uv.y+(smoothness/2.0)+1.0; y++)
        {
            randomSum += random(vec2(x, y));
        }
    }

    randomSum /= smoothness * smoothness;

    float rand = randomSum;
    vec3 randomcolor = vec3(rand, rand, rand);
    // Output to screen
    fragColor = vec4(randomcolor,1.0);
}
