#define rot2(a)      mat2(cos(a+vec4(0,11,33,0)))

void mainImage(out vec4 O, vec2 U)
{
    float t=9.,r;
    vec3  R = iResolution,
          D = normalize(vec3(U+U, -12.*R.y) - R),        // ray direction
          p = vec3(0,0,15), q, a,                        // marching point along ray 
          M = iMouse.z > 0. ? 6.*iMouse.xyz/R -3.        // camera control
              :  vec3( 1,-.5,0);

    for ( O=vec4(1); O.x > 0. && t > .01; O-=.01 )       // march scene
        q = p, 
        q.yz *= rot2(-M.y),                              // rotations
        q.xz *= rot2(-M.x-1.57),
        q = vec3( length(q.xz)-1., q.y, atan(q.z,q.x) ), // cylindrical coordinates
        q = vec3( length(q.xy), atan(q.y,q.x), q.z ),    // torus coordinates
        a = cos(q.z -iTime + vec3(0,11,0)), q.z += .5*iTime, // time modulators: radius, angle
        q.x -= .2*(.5+.5*a.x), q.z += .5*a.y,            // time modulation
        t = min(t, length( vec2(q.x, ( mod(16.*q.z-q.y,6.28)-3.14 ) /16. ) // soleno√Ød
                         ) - .05 ),
     // t = length(q.xy + .2 * (.5+.5*a.x)* cos(16.*q.z+8.*a.y+vec2(0,11)) ) -.05,
        p += .5*t*D;                                     // step forward = dist to obj    

   O *= O*O*O*1.6;                                       // color scheme 
// q.x = atan(q.y,q.x)/6.; q = sin(32.*q); t = q.x*q.z; O *= clamp(.5+t/fwidth(t),0.,1.);
}
