vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(fract((a1)*(a2.x+a2.y))-.5)
    ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec2 t2 = vec2(0.);
    for(int k = 0; k < 6; k++){
        //if(uv.y>0.) uv.x += .5;
        //else uv.y += .5;

        uv =
            abs(.5+uv+t2*sign(uv.x-uv.y))/1.5
        ;
        //uv += sign(uv-uv.yx)/2.+.5;
        if(uv.y>uv.x) uv = uv.yx;
        //else k += 1;

        t2 =
            -triangle_wave(uv-.5)
        ;
        uv =
            t2-triangle_wave(uv.yx)
        ;

        if(t2.y<uv.x)
        {t2 = t2.yx; uv=uv.yx+iTime/8.;}


        col =
            vec3(col.zx,max(abs(uv.x-uv.y)*sign(t2.x-t2.y),0.))
        ;
        //uv /= 1.+mod(uv.y,2.);
    }
    fragColor = vec4(col*2.,1.0);
}

float fmod(float a, float b){
    return
        mod(floor(a),b)
    ;
}
vec2 mainSound(int samp, float time){
  float tempo = 1.;
  time /= tempo;
  float s1 =
      4.
  ;
  float t=
      time/pow(2.,fmod(time/4.,3.))
  ,
  m1 =
      fmod(t*pow(2.,fmod(t/4.,3.)),4.)
  ,
  m3 = fmod(t*s1/pow(2.,fmod(t/4.,3.))/abs(2.5-m1),s1);
  t *=
      s1
  ;
  float m2 =
      1. + fmod((t/s1),s1)
  ;
  t /=
      max(m2,m1+1.)
  ;
  float a = 128.*sqrt((1.-sqrt(fract(t)))/s1)*.2
,
  nb = pow(2.,m3/2.+7.5)*tempo;
  return
      abs(.5-vec2(fract(time*nb*.998/2.),fract(time*nb/2.)))*a
  ;
}
