// color palette
vec3 a1 = vec3(1.000, 0.500, 0.500);
vec3 b1 = vec3(0.500, 0.500, 0.500);
vec3 c1 = vec3(0.750, 1.000, 0.667);
vec3 d1 = vec3(0.800, 1.000, 0.333);

vec3 palette(float t, vec3 a, vec3 b, vec3 c, vec3 d){
    return a + b*cos(6.28318*(c*t+d));
}

float remap(float value, float from1, float to1, float from2, float to2){
    return (value - from1) / (to1 - from1) * (to2 - from2) + from2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime * 0.8;

    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    //loop
    for(float i = 0.0; i < 7.0; i++)
    {
        float iSpeed1 = remap(i, 0., 5., 1., 2.);
        float rotateSpeed = 1.121 * iSpeed1;
        float sinTime = sin(time * rotateSpeed);
        float cosTime = cos(time * rotateSpeed);
        vec2 timeOffset = vec2(sinTime, cosTime);

        uv = fract(uv * (remap(sin(time*0.854 * rotateSpeed), -1., 1., 0.3, 1.5) * iSpeed1)) - 0.5;

        float d = length(uv) * exp(-length(uv0 + timeOffset*.5*iSpeed1/3.));

        //sample color palette
        float pT = length(uv0) + i*.4 + time*.4;
        vec3 col = palette(pT, a1*vec3(sin(time*0.75), cos(time*0.6654), cos(time*0.957)), b1, c1, d1);

        // sin
        float sinConst = 10.0;
        d = sin(d*sinConst + time) / sinConst;
        d = abs(d);

        // power for contrast
        d = pow(0.01 / d, 1.2) * remap(i, 0.0, 4.0, 1.0, 0.5);

        // layer it on
        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
