void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y ;

    float t = 2.5 * exp(0.15 * iTime);

    float d = length(uv);
    if(d < 0.3){
        d = 0.04 / mod(t,exp(-0.5 * d));
    }else{
        d = 0.04 / mod(t,sqrt(d));
    }
    fragColor = vec4(d * d, d, d, 1.0);
}
