void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 pos = (uv.xy - 0.5);
    vec2 cir = ((pos.xy * pos.xy + sin(uv.x * 18.0 + iTime) / 25.0 * sin(uv.y * 7.0 + iTime * 1.5) / 1.0) + uv.x * sin(iTime) / 16.0 + uv.y * sin(iTime * 1.2) / 16.0);
    float circles = (sqrt(abs(cir.x + cir.y * 0.5) * 25.0) * 5.0);

    vec3 color1 = vec3(1.0, 1.0, 1.0);  // White
    vec3 color2 = vec3(1.0, 1.0, 1.0);  // White
    vec3 color3 = vec3(1.0, 0.0, 0.0);  // Red

    vec3 color;
    if (circles < 3.0) {
        color = color1;
    } else if (circles < 6.0) {
        color = color2;
    } else {
        color = color3;
    }

    fragColor = vec4(color, 1.0);
}
