float random(vec2 _st, float t) {
  return fract(sin(dot(_st, _st) * 43708.0 + t));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float n = random(uv, iTime);

    vec3 color = vec3(1.0);

    fragColor = vec4(color * n,1.0);
}
