float sdHexagon( in vec2 p, in float r )
{
    const vec3 k = vec3(-0.866025404,0.5,0.577350269);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
}

vec3 palette( float t ) {
    vec3 a = vec3(1.198, 0.078, 0.468);
    vec3 b = vec3(0.108, 0.500, 0.500);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.000, 0.333, 0.667);

    return a + b*cos( 6.28318*(c*t+d) );
}

vec2 rotate(vec2 uv, float th) {
        return mat2(cos(th), sin(th), -sin(th), cos(th)) * uv;
}

vec3 drawScene(vec2 uv) {
    vec3 bg_color = palette(uv.x / uv.y * iTime * .01);
    // uv.x / uv.y provides a weird moire-like effect.

    vec3 finalColor = bg_color;

    vec3 color = vec3(0);
    for (float i = 0.0; i < 2.9; i++) {
        float hex = sdHexagon(uv, 0.45);
        vec3 hex_color = palette(hex - iTime*.04);
        uv = fract(uv * 2.) - 0.5;
        hex = sin(hex*8. + iTime)/8.;
        hex = abs(hex);
        hex = 0.02 / hex;
        color += hex * hex_color;
    }

    finalColor = mix(color, finalColor, -color);

    return finalColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    uv = rotate(uv, iTime*.04);

    vec3 col = drawScene(uv);

    fragColor = vec4(col,1.0);
}
