vec3 palette( in float t)
{
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.498, 1.338, 0.278);
    return a + b*cos( 6.28318*(c*t+d) );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 DisplayCol = vec3(0,0,0);




    for (float i = 0.0; i < 2.; i++){
        uv = fract(uv *(length(uv0)*(3.5*iTime*(0.9*sin(0.07*0.4)*sin(0.03*20.))))) -0.5;
        float d = length(uv);

    d = abs(d);
    d = smoothstep((1.*sin(0.5*300.0)), (1.*cos(0.9*sin(300.))), d);

    vec3 col = palette(length(uv0) + iTime);

    DisplayCol += col * cos(length(d));

   }

    // Output to screen
    fragColor = vec4(DisplayCol,1.0);
}
