void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float speed = -0.7;
    const float loop = log(3.0)/-speed;
    float time = mod(iTime, loop);

    vec2 uv = fragCoord/iResolution.xy;

    uv *= (pow(2.71828,time*speed));

    vec3 col = vec3(1.0);


    for(int i = 1; i<=8; i++) {
        if(mod(floor((uv.x)*pow(3.0, float(i))-1.0), 3.0) < 0.01 && mod(floor((uv.y)*pow(3.0, float(i))-1.0), 3.0) < 0.001){
            col = vec3(0.0);
        }
    }

    fragColor = vec4(col,1.0);
}
