void mainImage(out vec4 o, vec2 u) {
    vec4 D = vec4(u, 0, 0) / iResolution.xyyy - .5, 
         p, q, c;

    /* MOUSE!
	#define r(a) mat2(cos(a + vec4(0, 11, 33, 0)))
    if (length(iMouse.xy) > 40.) {
        float PI = atan(.0, -1.);
        D.yz *= r(-PI * .5 + iMouse.y/iResolution.y * PI);
        D.zx *= r(PI - iMouse.x/iResolution.x * PI * 2.);
    }
    */

    float i, d = 1.;

    p.z -= iTime;

    while(i++ < 100. && d > .01) {
        q = abs(mod(p, 8.) - 4.);

        o = c * 10. / i;

        c = texture(iChannel0, q.zxy - 3.);

        d = min(q.y, length(q.xz) - 2. + c.x);

        p += d * D;
    }
}
