#define S smoothstep
#define PI 3.14159265359

vec4 Line(vec2 uv, float speed, float height, vec3 col) {
    float waveAmplitude = 0.6; // 波の振幅

    uv.y += S(1., 0., abs(uv.x)) * sin(iTime * speed + uv.x * height) * waveAmplitude;

    // ラインの太さを制御
    float lineThickness = 0.016; // 例: 0.004から0.008に増加
    col = clamp(col * 2.9, 0.0, 1.0);  // 1.5は増加係数（調整可能）


    return vec4(S(.06 * S(.2, .9, abs(uv.x)), 0., abs(uv.y) - lineThickness) * col, 1.0) * S(1.1, 0.3, abs(uv.x));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 p = uv * 2. - 1.;
    p.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(0.0);


    for (float i = 0.; i <= 5.; i += 1.) {
        float t = i / 5.;
        col += Line(p, 1. + t, 4. + t, vec3(.2 + t * .7, .2 + t * .4, 0.3)).rgb;
    }

    vec4 lineColor1 = Line(p, 2.0, 6.0, vec3(1.0, 0.5, 0.0));
    col += lineColor1.rgb;


    vec4 lineColor2 = Line(p, 2.2, 5.5, vec3(0.5, 1.0, 0.0));
    col += lineColor2.rgb;


    float size = texture(iChannel0, vec2(0.1, 0.1)).x; 

    vec4 lineColor3 = Line(p, PI, size*2.2 , vec3(3.5, 8.7, 1.6)); 
    col += lineColor3.rgb;


   col /= 2.3;

    fragColor = vec4(col, 1.0);
}
