void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
//get the current time in seconds
#define iGlobalTime (float(iFrame)/60.0)
float time = iGlobalTime;

//normalize the fragCoord to be between -1 and 1
vec2 uv = fragCoord/iResolution.xy*2.0-1.0;
//flip the y coordinate
uv.y *= -1.0;

//create a 3D position from the 2D UV coordinates
vec3 pos = vec3(uv, 0.0);

//create a rotation matrix based on the time
mat3 rot = mat3(cos(time), -sin(time), 0.0,
                sin(time), cos(time), 0.0,
                0.0, 0.0, 1.0);

//rotate the position by the rotation matrix
pos = rot * pos;

//create a spinning 3D cube by rotating the position around the x and y axes
pos = mat3(cos(time), 0.0, -sin(time),
           0.0, 1.0, 0.0,
           sin(time), 0.0, cos(time)) * pos;
pos = mat3(1.0, 0.0, 0.0,
           0.0, cos(time), -sin(time),
           0.0, sin(time), cos(time)) * pos;

//draw the cube using a step function
fragColor = vec4(step(0.5, abs(pos.x)), step(0.5, abs(pos.y)), step(0.5, abs(pos.z)), 1.0);
}
