void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
//get the current time in seconds
#define iGlobalTime (float(iFrame)/60.0)
float time = iGlobalTime;

//normalize the fragCoord to be between -1 and 1
vec2 uv = fragCoord/iResolution.xy*2.0-1.0;
//flip the y coordinate
uv.y *= -1.0;

//create a 3D position from the 2D UV coordinates
vec3 pos = vec3(uv, 0.0);

//create a spinning sphere by rotating the position around the x and y axes
pos = mat3(cos(time), 0.0, -sin(time),
           0.0, 1.0, 0.0,
           sin(time), 0.0, cos(time)) * pos;
pos = mat3(1.0, 0.0, 0.0,
           0.0, cos(time), -sin(time),
           0.0, sin(time), cos(time)) * pos;

//add dots to the surface of the sphere using the sin function
vec3 pattern = vec3(sin(pos.x*10.0+time), sin(pos.y*10.0+time), sin(pos.z*10.0+time));

//draw the sphere using a smoothstep function
fragColor = vec4(vec3(smoothstep(0.45, 0.55, length(pos))) * pattern, 1.0);
}
