float f_n_dist_square(vec2 o){
    return max(abs(o.x), abs(o.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_trpino = (fragCoord.xy-iResolution.xy*0.5)/iResolution.y;
    float n_ndx_px = fragCoord.x + (fragCoord.y*iResolution.x);
    float n_ndx_px_nor = n_ndx_px / (iResolution.x*iResolution.y);
    float n_scl = 20.;
    o_trpino *= n_scl;
    vec2 o_reprate = vec2(1.);

    vec2 o = mod(o_trpino, o_reprate);
    o-=0.5;
    vec2 o_id = ((o_trpino - o)+n_scl)/(n_scl*2.);
    float n_id = o_id.x+(o_id.y*(1./n_scl/2.));
    // Time varying pixel color

    float n = f_n_dist_square(o);

    fragColor = vec4(
        sin(n*6.28*100.*n_id+iTime)
    );
}
