#define TRESHOLD 0.2
#define EROSION_PARAM 2

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;
    vec2 s  = 1./iResolution.xy;

    fragColor = vec4(texture(iChannel0,uv).xyz,1.);

    for(int i=-EROSION_PARAM; i<=EROSION_PARAM ;i++)
        for(int j=-EROSION_PARAM; j<=EROSION_PARAM ;j++)
            if(length(texture(iChannel0,uv+vec2(i,j)*s).xyz)<TRESHOLD) 
                fragColor = vec4(0);
}
