void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - .5;
    uv *= 2.;
    vec3 col;
    vec3 z = vec3(uv,uv.x);
    z.x *= iResolution.x/iResolution.y;
    z.z -= 1.2 * sin(iTime * 0.5);
    vec3 c = z;

    for(float i =0.;i<100.;i++){

        float r = length(z);

        float theta = acos(z.z/r);
		float phi = atan(z.y,z.x);
        float power = 8.;
        r = pow(r,power);
        theta *= power;
        phi *= power;
        z = r * vec3(cos(phi) * sin(theta),sin(phi) * sin(theta),cos(theta));
        z +=c;


    }
        if(length(z) < 2.)col.gb = vec2(1.);


    fragColor = vec4(col,1.0);
}
