#define PI     3.14159265

#define rot(v) mat2(cos(v), -sin(v), sin(v), cos(v))
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float coord_scale = cos(mod(iTime * 0.3, PI)) * 8. + 8.02;
    vec2 uv = fragCoord/iResolution.xy * coord_scale - coord_scale / 2.;

    float scale = abs(cos(iTime * 0.3));
    vec3 col = vec3(pow(scale, 11.));

    for (float i = 2.; i < 60. - length(uv) * 5.; i += 2.) {
        float x = uv.x + sin(i) * length(uv);
        float y = sin(x * 6. + i * 15.) / 3.1;

        float l = length(uv * rot(i * 3.));
        vec2 xy = vec2(x, y) * rot(l  * (1. + i * 0.5) + iTime);
        float d = length(uv - xy) * 10.;

        float r = 0.5 + 0.3 * sin(i * 1.4 + sin(length(uv) * 2.9 + i * 2.));
        float g = 0.5 + 0.3 * sin(i * 1.2 + sin(length(uv) * 3.1 + i * 3.));
        float b = 0.5 + 0.3 * sin(i * 1.9 + sin(length(uv) * 2.3 + i * 4.));
        float exp = 1./(0.4 + 0.8 * length(uv) * (1. + sin(length(uv) + i * 0.7) * 0.25));
        float ampl = 1. / (1. + i * (0.5 + sin(length(uv) * 3. + i) * 0.1));
        col += vec3(pow(r/d, exp), pow(g/d, exp), pow(b/d, exp)) * 0.5 * ampl;
    }

    vec3 final_col = col;

    fragColor = vec4(final_col,1.0);
}
