// golfed with help from FabriceNeyret2

#define H(a) (cos(vec3(0,1,2)+(a)*6.3)*.5+.5)  // hue
void mainImage( out vec4 c, vec2 u )
{
    c *= 0.;
    vec2 R = iResolution.xy;
    float m = iMouse.y/R.y, // mouse y
          p = 1.27202,      // sqrt(Phi) used as x offset
          t = iTime/5.,
          i = 0., r, z;

    if (m < .1) m = 0.;    // snap to 0 when close
    z = 1.-m;              // zoom
    u = (u+u-R)/R.y*1.2*z; // coords
    r = 1.-length(u);      // first circle

    for (; i++<20.;) // 1 iteration per row
        c.rgb += smoothstep(fwidth(r)*1.5, 0., abs(r))*(H(t*i)+.5)*.5 // circles
           + max(vec3(0), H(r+t*i)*r)*.3*z, // glow
        u *= 1.618034,                      // scale coords
        r = 1.-length(abs(u)-vec2(p, 0)),   // circle coords
        u.x = abs(u.x)-p;                   // offset

    c = c*c + c/4.;
}
