const float pi = 3.14159265f;

float fn(float x,float y,float n,float m) { 
    n *= pow(iTime,1.5);
    m *= pow(iTime,1.5);
    return sin(n*pi*x)*cos(m*pi*y) - sin(m*pi*x)*cos(n*pi*y); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    float v = fn( uv.x*5.f, uv.y*5.f, 1.f, 4.f ) / 2.f;
    vec3 col = vec3(v,0,v);

    fragColor = vec4(col,1.0);
}
