void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{   
    fragCoord += vec2(64., 0.);

	float x = fragCoord.x;
    float y = fragCoord.y;
    x /= 120.0;
    y /= 28.0;

    x *= sin(4.*x);
    x *= sin(1.*y);

    //x+=atan(0.1,3.0*0.1)*7.0;
    //x*= atan(x,y)...
    x*= atan(x,0.0)*sin(0.05+iTime/4.5)*2.0;


	fragColor = vec4(sin(x*0.0012) + sin(x*1.003), sin(x*1.006), sin(x*1.008), 1.0);
}
