// Fork of "2D Water Wave Test" by Sleicreider. https://shadertoy.com/view/MsKXDh
// 2023-02-21 03:56:30

#define PI 3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
            float time = iTime*2.0*PI;

            vec2 p = fragCoord/iResolution.xy;
            vec2 w1 = p * 15. + time * 2.;
            p += vec2(sin(w1.y), cos(w1.x))/iResolution.xy;
            float w2 = (p.x + p.y)*15. + time * 2.;
            p += vec2(sin(w2), cos(w2))/(iResolution.xy * vec2(0.16, 0.3) + 2. * sin(time));

            fragColor =  texture(iChannel0, p);
}
