vec3 getTex(vec2 uv){
	return texture(iChannel0,uv).rgb;    
}


vec3 crossBlur(vec2 uv, int steps){

  float px = 1.0/iResolution.x;
  float py = 1.0/iResolution.y;

  // distubution weight of pixels
  float weight = 1.0/float((steps*4)+1);

  vec3 c = getTex(uv)*weight;

  for(int i=0; i<steps; i++){

      float k =float(i);
      vec2 left = uv+ vec2(-k*px,0);
      vec2 right = uv+ vec2(k*px,0);
      vec2 up = uv+ vec2(0,k*py);
      vec2 down = uv+ vec2(0,-k*py);

      c+= getTex(left)*weight;
      c+= getTex(right)*weight;
      c+= getTex(up)*weight;
      c+= getTex(down)*weight;

  }

  return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;


    float split = 0.0;
    if(uv.x-0.5 > 0.0){
        split=1.0;
    }

    vec3 col = crossBlur(uv,5)*split;


    // blur the right half 
    fragColor = vec4((1.0-split)*getTex(uv)+col,1.0);
}
