void mainImage( out vec4 o, in vec2 FC )
{
float i,d=.1,e,g=mod(12.+iTime,24.);vec3 p;
o=vec4(0.);

for(float i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    p=vec3(log(length(p)),cos(p.z-g),atan(p.x,p.y)*g*.1);
    p=abs(abs(mod(p, .8))-.6)-.5;
    d=pow(length(p.z-p.x),3.);
    d-=dot(p,p)/2e4;
    e+=d;
}o+=vec4(p,1)*3./i;
}
