void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;



    vec4 tex = texture(iChannel0, uv);

    // normalize origin
    vec2 origin = uv * 2.0 - 1.0;
    origin.x *= iResolution.x / iResolution.y;

    // move origin
    float ySpeed = sin(iTime * 0.0012) * 5.0 + 5.0;
    float xSpeed = sin(iTime * 0.008) * 1.5 + 1.5;
    origin.y += sin(iTime * ySpeed);
    origin.x += cos(iTime * xSpeed);

    // draw circle on origin
    float radius = (sin(iTime* 3.0) + 1.5) * 0.8;
    float circle = 1. - length(origin) * (1. / radius);
    circle = smoothstep(0.3, 0.5, circle);


    tex *= circle;

    vec4 grading = vec4(1.0, 0.95, 0.8, 1.0);
    tex.r = tex.r * 1.2;
    tex.g = tex.g * 1.1;
    tex.b = tex.b * 0.9;

    // Combine grading and original texture
    tex = mix(tex, grading, 0.2);

    // Apply vignette
    float vignette = 0.5 + 0.5 * pow(length(origin), 0.5);
    tex.rgb *= vignette;

    float noise = fract(sin(dot(fragCoord.xy + vec2(0.0, iTime), vec2(12.9898, 78.233))) * 43758.5453);

    // Define the mix factor (0.0 to 1.0)
    float mixFactor = .1;  // Adjust this value

    // Mix the noise and the original texture
    vec3 noiseColor = vec3(noise);
    tex.rgb = mix(tex.rgb, noiseColor, mixFactor);

    // Output to screen
    fragColor = tex;
    //fragColor = vec4(vec3(circle), 1.0);
}
