void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // 将屏幕坐标转换为纹理坐标
    vec2 uv = fragCoord / iResolution.xy;

    // 计算彩虹颜色过渡
    vec3 rainbowColor = vec3(0.0);
    if (uv.x < 1.0/10.0) {
        // 蓝色到绿色
        rainbowColor =mix(vec3(0.0, 0.0, 1.0), vec3(0.0, 1.0, 0.0), uv.x * 10.0);
    } else if (uv.x < 2.0/10.0) {
        // 绿色到深绿
        rainbowColor = mix(vec3(0.0, 1.0, 0.0), vec3(0.0, 0.5, 0.0), (uv.x - 1.0/10.0) * 10.0);
    } else if (uv.x < 3.0/10.0) {
        // 深绿到黄色
        rainbowColor = mix(vec3(0.0, 0.5,0.0), vec3(1.0, 1.0, 0.0), (uv.x - 2.0/10.0) * 10.0);
    } else if (uv.x < 4.0/10.0) {// 黄色到橙色
        rainbowColor = mix(vec3(1.0, 1.0, 0.0), vec3(1.0, 0.5, 0.0), (uv.x - 3.0/10.0) * 10.0);
    } else if (uv.x < 5.0/10.0) {
        // 橙色到红色
        rainbowColor = mix(vec3(1.0, 0.5, 0.0),vec3(1.0, 0.0, 0.0), (uv.x - 4.0/10.0) * 10.0);
    } else if (uv.x < 6.0/10.0) {
        // 红色到深红
        rainbowColor = mix(vec3(1.0, 0.0, 0.0), vec3(0.5, 0.0, 0.0), (uv.x - 5.0/10.0) * 10.0);} else if (uv.x < 7.0/10.0) {
        // 深红到粉色
        rainbowColor = mix(vec3(0.5, 0.0, 0.0), vec3(1.0, 0.0, 1.0), (uv.x - 6.0/10.0) * 10.0);
    } else if (uv.x < 8.0/10.0) {
        // 粉色到紫色
        rainbowColor = mix(vec3(1.0, 0.0, 1.0), vec3(0.5,0.0, 0.5), (uv.x - 7.0/10.0) * 10.0);
    } else if (uv.x < 9.0/10.0) {
        // 紫色到深紫
        rainbowColor = mix(vec3(0.5, 0.0,0.5), vec3(0.2, 0.0, 0.2), (uv.x - 8.0/10.0) * 10.0);
    } else {
        // 深紫到蓝色
        rainbowColor =mix(vec3(0.2, 0.0, 0.2), vec3(0.0, 0.0, 1.0), (uv.x- 9.0/10.0) * 10.0);
    }

    // 输出最终颜色
    fragColor = vec4(sqrt(rainbowColor), 1.0);
}
