void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy / iResolution.xy) - 0.5;
    uv.x *= iResolution.x / iResolution.y;
    float zoom = 1.5 + 1.5;
    vec2 c = vec2(0.0, 0.0) + vec2(sin(iTime), cos(iTime)) * 0.75;
    vec2 z = vec2(uv.x, uv.y) * zoom;
    float intensity = 0.00001;
    float iterations = 100.0;
    for (float i = 0.0; i < iterations; i++) {
        if (length(z) > 4.0) {
            break;
        }
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;
        intensity += 1.0 / iterations;
    }
    intensity = exp(2.0 * intensity) - 1.0;
    fragColor = vec4(intensity * vec3(1.0, 1.0, 1.0), 1.0);
}
