void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = vec2(2.0*fragCoord.x-iResolution.x, 2.0*fragCoord.y-iResolution.y)/iResolution.y;
    float r = length(uv); // distance from center
    float noise = fract(sin(dot(uv,vec2(12.9898,78.233))) * 43758.5453); // Perlin noise
    float cloud = smoothstep(0.2, 0.6, noise + 0.5 - r*2.0); // cloud function
    fragColor = mix(vec4(0.5, 0.5, 1.0, 1.0), vec4(1.0, 1.0, 1.0, 1.0), cloud); // set color based on cloud function
}
