int H(ivec3 x)  {
    int k = 1103515245; 
        x = ((x>>8)^x.yzx)*k;
        x = ((x>>8)^x.yzx)*k;  
    return (((x>>8)^x.yzx)*k).x;
}

void mainImage( out vec4 o, in vec2 I ) {
    vec3  t = iResolution, m;
    float s = sqrt(3.), e = s/.1/t.y;
    I = ( I+I - t.xy ) / t.y; 

    t = vec3( vec2(-s,s) * I.x - I.y + .1, I.y*2. ) / .2;
 // t = I * mat3x2(-s,-1,s,-1,0,2)/.2 + vec3(.5,.5,0);   // variant
    int i = H( ivec3(m=floor(t)) + int(iTime) ) % 3;
    t = fract( m.x + m.y + m.z == 0. ? t : -t );
    t = i>1 ? t.zxy : i>0 ? t.yzx : t;
    o = vec4( sqrt( smoothstep(e,-e, min(min(abs(t.y-t.x),
                                             abs(2.*t.z)),
                                             abs(2.*t.z-1.))
                                     -.1)));
}
