void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - .5 * iResolution.xy) / iResolution.y;

    vec2 st = vec2(atan(uv.x, uv.y), length(uv));

    uv = vec2(st.x/6.2831 + .50 + iTime *.1, st.y);


    // Flower Big
    float x_1 = uv.x * 7.;

    float m_1 = min(fract(x_1), fract(1.-x_1));

    float flow_1 = smoothstep(0., .1,m_1 *.25 +.25 -uv.y);

    // Flower Medium
    float x_2 = uv.x * 14.;

    float m_2 = min(fract(x_2) / 1.25, fract(1.-x_2));

    float flow_2 = smoothstep(0., .1,m_2 *.2 +.2 - uv.y);

    // Flower Small
    float x_3 = uv.x * 21.;

    float m_3 = min(fract(x_3), fract(1.- x_3));

    float flow_3 = smoothstep(0., .1,m_3 *.15 +.15 -uv.y);


    // Flower Circle
    float x_4 = uv.x;

    float circle = smoothstep(0., .1,.1 -uv.y);


    float flow_t = flow_1 * .45 + flow_2 * .35 + flow_3 * .15 + circle * 0.5;

    // Output to screen
    fragColor = vec4(flow_t, flow_t, 0., 0.) * min(iTime / 20., 1.);
}
