void mainImage(out vec4 o, vec2 u)
{
    u = (u*2.-iResolution.xy)/iResolution.y;

    o = sin(length(u)*vec4(9, 10, 11, 0)-iTime)*.5+.5;
    float h = .5-u.x*u.x*u.x*u.x*.04;
    o *= smoothstep(h, h-.5,  u.y)*smoothstep(h, h-.5, -u.y);

    o = 3.*o*o-2.*o*o*o;
}


