void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord  - .5 * iResolution.xy)/iResolution.y;
    uv *= 2.;
    vec3 col;

    float detail = cos(iTime * 0.1) * 100.;
    vec2 z = floor(uv * detail) / detail;
    vec2 c = z;
    for(int i = 0;i < 100;i++){
        z = vec2(z.x * z.x - z.y * z.y,2. * z.x * z.y) + c;
    }
    col.rg += sin(length(z * 150. * 15. * iTime));
    col.gb += cos(length(z * 199. * 10. * iTime));
    col+= sin(length(z * 20. * iTime));
    fragColor = vec4(col,1.0);
}
