#define PI 3.141592

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;


    float time = iTime * 1.8;
    float light = abs(sin(time * 0.6)) * 0.7;

    vec3 inside_color = vec3(0.0);
    vec2 center = vec2(0.5 * (iResolution.x/iResolution.y), 0.5);
    vec2 uvvector = uv - center;

    float rdot = dot(normalize(uvvector), normalize(vec2(sin(time), cos(time))));
    inside_color.r += rdot * rdot * (3.0 - 2.0 * rdot);

    float gdot = dot(normalize(uvvector), normalize(vec2(sin(time + 2.0*PI*0.333), cos(time + 2.0*PI*0.333))));
    inside_color.g += gdot * gdot * (3.0 - 2.0 * gdot);

    float bdot = dot(normalize(uvvector), normalize(vec2(sin(time + 2.0*PI*0.666), cos(time + 2.0*PI*0.666))));
    inside_color.b += bdot * bdot * (3.0 - 2.0 * bdot);

    inside_color += light;

    float r = 0.3;
    vec3 col = smoothstep(1.5/iResolution.y, 0.0, distance(uv, center) - r) * inside_color;

    // Output to screen
    fragColor = vec4(col,1.0);
}
