void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Normalization of space.
    vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y;

    //Color
    vec3 pink = vec3(0.957,0.620,1.000);

    //Code
    float d = length(uv);
    d = sin(d*10.+iTime)/iTime;
    d = abs(d);
    d = step(0.05,d);
    pink *= d;
    fragColor = vec4(pink,1.0);
}
