void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 a_n_pixel_coords_norm = fragCoord.xy / iResolution.xy; 
    vec2 a_n_mosue_coords_norm = iMouse.xy / iResolution.xy; 
    if(iMouse.xy==vec2(0)){
        a_n_mosue_coords_norm = vec2(0.5,0.5);
    }
    //if(iMouse.z == (0.0)){
        // left mouse button is not hold down
        //a_n_mosue_coords_norm = vec2(0.5,0.5);
    //}

    float n_radius = 1.0;

    float n_tau = 6.2831;
    float n_amp = 0.01;
    vec2 a_n_translation = vec2(0.0,0.0);
    fragColor = vec4(1.0);

    float n_waves = 5.0;
    float n_any = 100.0 * a_n_mosue_coords_norm.x;
    float n_mouse_y_factor = 11.0 * a_n_mosue_coords_norm.y;
    float n_mouse_x_factor = float(int(11.0 * a_n_mosue_coords_norm.y));

    float n_x_drops = n_mouse_x_factor;
    float n_y_drops = n_mouse_x_factor;
    for(float n_x_drop = 0.0; n_x_drop < n_x_drops; n_x_drop+=1.0){
        for(float n_y_drop = 0.0; n_y_drop < n_y_drops; n_y_drop+=1.0){

            float n_distance = length(
                vec2(
                    (1.0/n_x_drops)*n_x_drop+(1.0/n_x_drops/2.0),
                    (1.0/n_y_drops)*n_y_drop+(1.0/n_y_drops/2.0)
                ) 
                -
                a_n_pixel_coords_norm
            );
            n_distance += mod(iTime*0.1, 1.0);
            float n_mod_max = 1.0/n_waves; 
            float n_dist_mod = mod(n_distance, n_mod_max);
            float n_dist_mod_norm = n_dist_mod / n_mod_max;

            float n_dist_mod_norm_sin = sin(n_dist_mod_norm*n_tau)*0.5+0.5;

            fragColor *= vec4(1.0-n_dist_mod_norm_sin*n_any);
        }
    }




}
