void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    // limit rendering to the lower left of the screen
    const float size = 258.0;    
    if (fragCoord.x > size || fragCoord.y > size)
    {
        fragColor = vec4(0.0);
        return;
    }
    mat2 rotationMatrix = mat2(cos(iTime*3.0), sin(iTime*3.0),-sin(iTime*3.0), cos(iTime*3.0));
    vec2 pivot = vec2( 0.3, 0.3);
    // calculate uv
	vec2 uv = fragCoord/size;
    uv = rotationMatrix * (uv - pivot) + pivot;
    // sample texture
    fragColor = texture(iChannel0,uv+vec2(iTime, 0.0));
}
